import { redirect } from 'next/navigation';
import { ImageResponse } from 'next/og';
import { ImageResponseOptions, NextRequest } from 'next/server';
import React from 'react';

import { getContentTitle } from '@/lib/utils';

import { PageIdParams, fetchPageData } from '../../../../fetch';
import { absoluteHref } from '@/lib/links';
import { tcls } from '@/lib/tailwind';
import { CustomizationHeaderPreset } from '@gitbook/api';
import colorContrast from 'postcss-color-contrast/js';

export const runtime = 'edge';

// TODO: Support all fonts available in GitBook
// Right now this is impossible since next/font/google does not expose the cached font file
// Another option would be to use the Satori prop `loadAdditionalAsset` [example](https://github.com/vercel/satori/blob/main/playground/pages/index.tsx),
// but this prop isn't (yet) exposed through `ImageResponse`.
const interRegular = fetch(
    new URL('../../../../../../fonts/Inter/Inter-Regular.ttf', import.meta.url),
).then((res) => res.arrayBuffer());
const interBold = fetch(
    new URL('../../../../../../fonts/Inter/Inter-Bold.ttf', import.meta.url),
).then((res) => res.arrayBuffer());

// By passing ImageResponse (Satori) a global object we make it cachable.
// This also caches the font data, speeding up rendering.
const imageOptions: ImageResponseOptions = {
    width: 1200,
    height: 630,
    fonts: [
        {
            name: 'Inter',
            data: await interRegular,
            weight: 400,
            style: 'normal',
        },
        {
            name: 'Inter',
            data: await interBold,
            weight: 700,
            style: 'normal',
        },
    ],
};

/**
 * Render the OpenGraph image for a space.
 */
export async function GET(req: NextRequest, { params }: { params: PageIdParams }) {
    const { space, page, customization, parent } = await fetchPageData(params);

    if (customization.socialPreview.url) {
        // If user configured a custom social preview, we redirect to it.
        redirect(customization.socialPreview.url);
    }

    const theme = customization.themes.default;
    const light = theme === 'light';

    const bold = customization.header.preset == CustomizationHeaderPreset.Bold;
    const custom = customization.header.preset == CustomizationHeaderPreset.Custom;

    // We have no access to CSS variables, so we'll have to hardcode some values
    const baseColors = {
        light: '#fff',
        dark: '#111827',
    };

    const colors = {
        primary: custom
            ? customization.header.linkColor?.[theme]
            : bold
              ? colorContrast(customization.styling.primaryColor[theme], [
                    baseColors.light,
                    baseColors.dark,
                ])
              : customization.styling.primaryColor[theme],
        background: custom
            ? customization.header.backgroundColor?.[theme]
            : bold
              ? customization.styling.primaryColor[theme]
              : baseColors[theme],
        foreground: custom
            ? customization.header.linkColor?.[theme]
            : bold
              ? colorContrast(customization.styling.primaryColor[theme], [
                    baseColors.light,
                    baseColors.dark,
                ])
              : baseColors[light ? 'dark' : 'light'],
    };

    const favicon = function () {
        if ('icon' in customization.favicon)
            return (
                <img
                    src={customization.favicon.icon[theme]}
                    width={40}
                    height={40}
                    tw={tcls('mr-4')}
                />
            );
        if ('emoji' in customization.favicon)
            return (
                <span tw={tcls('text-4xl', 'mr-4')}>
                    {String.fromCodePoint(parseInt('0x' + customization.favicon.emoji))}
                </span>
            );
        return (
            <img
                src={absoluteHref(
                    `~gitbook/icon?size=medium&theme=${customization.themes.default}`,
                    true,
                )}
                width={40}
                height={40}
                tw={tcls('mr-4')}
            />
        );
    };

    return new ImageResponse(
        (
            <div
                tw={tcls(
                    'justify-between',
                    'p-20',
                    'relative',
                    'w-full',
                    'h-full',
                    'flex',
                    'flex-col',
                    `bg-[${colors.background}]`,
                    `text-[${colors.foreground}]`,
                )}
            >
                {/* Gradient */}
                <div
                    tw={tcls('absolute', 'inset-0')}
                    style={{
                        backgroundImage: `radial-gradient(ellipse 100% 100% at top right , ${bold ? colors.foreground : colors.primary}, ${bold ? colors.foreground : colors.primary}00)`,
                        opacity: 0.5,
                    }}
                ></div>

                {/* Grid */}
                <img
                    tw={tcls('absolute', 'inset-0', 'w-[100vw]', 'h-[100vh]')}
                    src={
                        light
                            ? 'data:image/png;base64,'
                            : 'data:image/png;base64,'
                    }
                    alt="Grid"
                />

                {/* Logo */}
                {customization.header.logo ? (
                    <img
                        alt="Logo"
                        height={90}
                        src={
                            light ? customization.header.logo.light : customization.header.logo.dark
                        }
                    />
                ) : (
                    <div tw={tcls('flex')}>
                        {favicon()}
                        <h3 tw={tcls('text-4xl', 'my-0')}>
                            {getContentTitle(space, customization, parent)}
                        </h3>
                    </div>
                )}

                {/* Title and description */}
                <div tw={tcls('flex', 'flex-col')}>
                    <h1
                        tw={tcls(
                            'text-8xl',
                            'my-0',
                            'tracking-tight',
                            'leading-none',
                            'text-left',
                            `text-[${colors.primary}]`,
                            'font-bold',
                        )}
                    >
                        {page
                            ? page.title.length > 65
                                ? page.title.slice(0, 65) + '...'
                                : page.title
                            : 'Not found'}
                    </h1>
                    {page?.description && page?.title.length <= 65 ? (
                        <h2 tw={tcls('text-4xl', 'mb-0', 'mt-8', 'w-[75%]', 'font-normal')}>
                            {page.description.length > 140
                                ? page.description.slice(0, 140) + '...'
                                : page.description}
                        </h2>
                    ) : null}
                </div>
            </div>
        ),
        imageOptions,
    );
}
